import React, {Component} from 'react';
import '../css/Content-homepage.css';
import "antd/dist/antd.css";
import {Row, Col} from 'antd';
import {Container,} from 'react-bootstrap';
import LayoutHeader from "../../layout/js/Layout-header";
import LayoutFooter from "../../layout/js/Layout-footer";
import axios from "axios";

class ContentHompage extends Component {
    constructor(props)
    {
        super(props);
        this.state = {
            news: [],
            hasMore: true,
            loading: false,
            api: "https://nalvnsmartnews.herokuapp.com/api/news"
        };
    }

    getNews = ()=>{
        axios.get(this.state.api)
            .then((response) => {

                const getNews = response.data.data.data;
                this.setState({
                    category : getNews
                })
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    componentDidMount() {
        this.getNews();
    }

    renderNews =()=> {
        return this.state.category.map((value, index) => {
                console.log(value.name)
        });
    }

    render() {

        return (
            <div>
                <LayoutHeader/>
                <Container className="mt-2">
                    <Row>
                        <Col lg={16} className="featured-news">
                            <Row className="featured-news-top">

                                <Col lg={16} className="featured-news-hot-top-fl">
                                    <a href="#">
                                        <img className="img-news-hot-top-fl"
                                             src="https://kenh14cdn.com/2019/2/26/photo-1-1551159187455835663384.jpg"
                                        />
                                    </a>

                                    <h3 className="title-news-hot-top-fl">
                                        <a href="#">Chân dung em gái chủ tịch Kim - người cẩn thận thị sát sân ga Đồng Đăng trước khi anh trai xuống tàu</a>
                                    </h3>


                                </Col>

                                <Col lg={8} className="featured-news-hot-top-fr pl-lg-1 pr-lg-1">
                                    <a href="#">
                                        <img className="img-fluid"
                                             src="https://res.cloudinary.com/aptech-fpt/image/upload/v1549962244/hihi.jpg"
                                             alt="#"/>
                                    </a>
                                    <h3 className="title-news-hot-top-fr">
                                        <a href="#">
                                            Đêm nay, giải bóng đá danh giá nhất hành tinh dành cho cấp câu lạc bộ chính
                                            thức trở lại
                                        </a>
                                    </h3>
                                </Col>
                            </Row>

                            <Row className=" mt-2">
                                <ul className="list-group list-group-flush">

                                    <li className="p-0 list-group-item">
                                        <Row>
                                            <Col lg={6} sm={8} xs={10} className="pr-lg-2  pr-sm-2 pr-xl-2">
                                                <a href="#"><img className="huy123" src="https://znews-photo.zadn.vn/w660/Uploaded/lce_vjrcc/2019_02_26/Tbtcpc.jpg"
                                                 alt=""/></a>
                                            </Col>

                                            <Col lg={18} sm={16}>

                                                <h4 className="title-text-item-content-fl">
                                                    <a href="#">TBT, Chủ tịch nước Nguyễn Phú Trọng hội đàm Quốc vương Campuchia</a>
                                                </h4>

                                                <div className="text-item-content-fl">
                                                    <div className="categoty-item-content-fl mb-lg-1">
                                                        <a className="" href="#">Thế giới</a>
                                                    </div>
                                                    <span className="description-item-content-fl">
                                                        Chiều 25/2, sau Lễ đón chính thức, tại Chính Điện Hoàng Cung ở Phnom Penh, Tổng bí thư, Chủ tịch nước Nguyễn Phú Trọng đã hội đàm với Quốc vương Campuchia Norodom Sihamoni.
                                                    </span>
                                                </div>
                                            </Col>
                                        </Row>
                                    </li>
                                </ul>
                            </Row>
                        </Col>

                        <Col lg={8} className="featured-banner-hot-top-fr pr-lg-0">
                            <Row>
                                <ul className="list-group list-group-flush">
                                    <li className="list-group-item">
                                        <Row>
                                            <Col lg={9} className=" pr-lg-1">
                                                <a href="#"><img className="img-fluid"
                                                                 src="https://znews-photo.zadn.vn/w480/Uploaded/sgtnrb/2019_02_26/621895cfc4c726997fd6.jpg"
                                                                 alt=""/></a>
                                            </Col>

                                            <Col lg={13}>
                                                <h4 className="title-text-item-content-fl">
                                                    <a href="#">Ông Trump sẽ gặp riêng và ăn tối với ông Kim vào ngày
                                                        mai</a>
                                                </h4>
                                            </Col>
                                        </Row>
                                    </li>
                                </ul>
                            </Row>

                            <Row>
                                <Col lg={24} className="ml-lg-4 banner-right">
                                    <a href="#">
                                        <img className="img-fluid"
                                             src="https://res.cloudinary.com/aptech-fpt/image/upload/v1550772078/banner_hcm_02-min.png"
                                             alt=""/>
                                    </a>
                                </Col>
                            </Row>
                        </Col>
                    </Row>
                </Container>
                <LayoutFooter/>
            </div>
        );
    }
}

export default ContentHompage;
