import React, { Component } from 'react';
import "antd/dist/antd.css";
import "../css/Content-Detail.css"
import axios from "axios";
import {Col, Row} from "antd";
import {Container} from "react-bootstrap";


class ContentDetail extends Component{
    constructor(props){
        super(props);
        this.state = {
            news :[],
            newsId: undefined,
            api:"https://smartnews.nal.vn/api/news/"
        }
    }

    getId = ()=>{
        this.setState({
            newsId : this.props.match.params.newsId
        })
        // console.log(this.props.match.params.newsId);
        // console.log(`${this.state.api}${this.props.match.params.newsId}`)
    }

    getNews = () => {
        axios.get(`${this.state.api}${this.state.newsId}`)
            .then((response) => {
                const getNews = response.data.data.content;
                // this.setState({
                //     news:getCategory
                // })
                console.log(getNews)
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    componentWillMount() {
        this.getId();
    }

    componentDidMount() {
        this.getNews();
    }

    render() {
        return(
            <div>
                <Container id="detail-content" className="news-detail-wrapper ">
                    <div className="news-detail-container">
                        <Row>
                            <Col lg={16}>
                                <div className="title-content-detail">
                                    <h3 className="font-weight-bold">
                                        Title News Content
                                    </h3>
                                </div>
                                <div className="img-news-detail">
                                    <img className="img-fluid" src="http://kenh14cdn.com/crop/640_360/2019/3/4/artboard-1-copy-21-15517180620582023557091-crop-1551718086209525342569.png" alt=""/>
                                </div>
                                <div className="description-news-detail">
                                    <span className="font-weight-bold">
                                        Trong lúc vợ chồng "vua cà phê" Việt Nam vẫn còn đang tranh chấp từng phần trăm cổ phần, thì ở một góc nhỏ đâu đó Sài Gòn có "ông vua cà phê vỉa hè" đang tất bật chuẩn bị sang Mỹ thăm con gái. Với người đàn ông này con cái chính là tài sản quý giá nhất của bậc sinh thành.
                                    </span>
                                </div>
                                <div className="content-news-detail">
                                    <span>
                                        Trong lúc vợ chồng "vua cà phê" Việt Nam vẫn còn đang tranh chấp từng phần trăm cổ phần, thì ở một góc nhỏ đâu đó Sài Gòn có "ông vua cà phê vỉa hè" đang tất bật chuẩn bị sang Mỹ thăm con gái. Với người đàn ông này con cái chính là tài sản quý giá nhất của bậc sinh thành.
                                    </span>
                                </div>
                            </Col>
                        </Row>
                    </div>
                </Container>
            </div>
        );
    }

}

export default ContentDetail;
