import React, { Component } from 'react';
import axios from 'axios';


class NewsABC extends Component{
    constructor(props){
        super(props);
        this.state={
            news:[],
        };
    }

    getNews = ()=>{
        axios.get(`https://nalvnsmartnews.herokuapp.com/api/category/news/${this.props.match.params.userId}`)
            .then((response) => {
                const getCategory = response.data.data;
                console.log(getCategory);
                this.setState({
                   news : getCategory
                });
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    componentWillMount() {
        // this.setAPI();
    }


    componentDidMount() {
         this.getNews();
    }

    render() {
        console.log(this.props.match.params.userId);
        console.log(this.props.match.url);

        return(
            <div>
                <h3>news1</h3>
            </div>
        );
    }
}
export default NewsABC;
