import React, {Component} from 'react';
import "antd/dist/antd.css";
import Container from "react-bootstrap/es/Container";
import Row from "react-bootstrap/es/Row";
import Col from "react-bootstrap/es/Col";
import createHistory from 'history/createBrowserHistory'
import axios from "axios";


class demoHistory extends Component{
    constructor(props){
        super(props);
        this.state = {
            news: [],
            newsId: undefined,
            api: "https://smartnews.nal.vn/api/news/",
            categorys: [],
            categoryId: undefined,
            apiCategory: "https://smartnews.nal.vn/api/category/news/",
        }
    }

    getId = () => {
        this.setState({
            newsId: this.props.match.params.demoId
        })
    }

    getNews = () => {
        const history = createHistory()
        const unlisten = history.listen((location, action) => {
            // location is an object like window.location
            console.log(action, location.pathname, location.state)
        })



        axios.get(`${this.state.api}${this.state.newsId}`)
            .then((response) => {

                const getNews = response.data.data;
                const getCategorys = response.data.data.category_id;
                const hehe = response.data.data.title;
                console.log(hehe);
                // history.push(`news/${hehe}`, { some: 'state' });
                 unlisten();

                this.setState({
                        news: getNews,
                        categoryId: getCategorys
                    }, this.getCategorys,
                    this.topFunction(),
                )
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    componentWillMount() {
        this.getId()
    }

    componentDidMount() {
        this.getNews()
    }

    render() {
        return(
            <div>
                <Container>
                    <Row>
                        <Col>
                            <h3>hihi</h3>
                            <h3>hihi</h3>
                            <h3>hihi</h3>
                        </Col>
                    </Row>
                </Container>
            </div>
        )
    }
}
export default demoHistory;
