import React, { Component } from 'react';
import axios from 'axios';


class NewsABC extends Component{
    constructor(props){
        super(props);
        this.state={
            news:[],
            api:`https://nalvnsmartnews.herokuapp.com/api/category/news/${this.props.match.params.userId}`,
            categoryId: null
        };
    }

    setAPI = () => {

        console.log(this.props.match.params.userId);
        this.setState({
            api : `https://nalvnsmartnews.herokuapp.com/api/category/news/${this.props.match.params.userId}`
            })
    }

    getNews = ()=>{
        axios.get(this.state.api)
            .then((response) => {
                const getCategory = response.data.data;
                console.log(getCategory);
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    componentWillMount() {
        this.setAPI();
    }


    componentDidMount() {
        this.getNews();
    }

    render() {

        console.log(this.state.api);

        return(
            <div>
                <h3>news1</h3>
            </div>
        );
    }
}
export default NewsABC;
