import React, { Component } from 'react';
import "antd/dist/antd.css";
import "../css/Content-Detail.css"
import axios from "axios";
import {Col, Row} from "antd";
import {Container} from "react-bootstrap";
import html from 'react-inner-html';
import {Link} from "react-router-dom";

class ContentDetail extends Component{
    constructor(props){
        super(props);
        this.state = {
            news :[],
            newsId: undefined,
            api:"https://nalvnsmartnews.herokuapp.com/api/news/",
            categorys:[],
            categoryId:undefined,
            apiCategory:"https://nalvnsmartnews.herokuapp.com/api/category/news/"
        }
    }
    getId = ()=>{
        this.setState({
            newsId : this.props.match.params.newsId
        })
    }

    getNews = () => {
        axios.get(`${this.state.api}${this.state.newsId}`)
            .then((response) => {
                const getNews = response.data.data;
                const lol = response.data.data.category_id;
                this.setState({
                    news:getNews,
                    categoryId: lol
                })
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    componentWillMount() {
        this.getId()
    }

    componentDidMount() {
         this.getNews()
    }

    componentWillUpdate(nextProps, nextState, nextContext) {
        if (nextState.categoryId !== this.state.categoryId){
            axios.get(`${this.state.apiCategory}${nextState.categoryId}`)
                .then((response) => {
                    const getCategory = response.data.data.data;
                    this.setState({
                        categorys: getCategory,
                    })
                    this.haha()
                })
                .catch(function (error) {
                    console.log(error);
                });
        } else {
            console.log("huyhuy")
        }
    }

    renderDetailNews = ()=>{
        if (this.state.news !== null){
            return(
                <div>
                    <div className="title-content-detail ">
                        <div>
                            <h3 className="text-title-content-detail font-weight-bold">
                                {this.state.news.title}
                            </h3>
                        </div>
                    </div>
                    <div className="img-news-detail pb-2">
                        <img className="img-fluid" src={this.state.news.img} alt=""/>
                    </div>

                    <div className="description-news-detail pt-2 pb-2">
                        <span className="font-weight-bold">
                            {this.state.news.description}
                        </span>
                    </div>

                    <div className="content-news-detail pt-2 pb-2">
                        <div {...html(this.state.news.content)} />
                    </div>
                </div>
            )
        }
    }

    haha = ()=>{
       const hehe = this.state.categorys.slice(0,6);
       return(
           <div>
               
           </div>
       );
    }

    // renderCategoryNews = ()=>{
    //     // const hehe = this.state.categorys.slice(0,6);
    //     console.log(this.state.categorys)
    // }

    render() {
        return(
            <div >
                <Container id="detail-content" className="news-detail-wrapper mt-2">
                    <div className="news-detail-container" >
                        <Row>
                            <Col lg={16} >
                                <Row>
                                    <Col>
                                        {this.haha()}
                                    </Col>
                                </Row>
                                <Row>
                                    <Col>
                                        <div className="news-of-category">
                                            <div>
                                                <h5 className="category-of-detail text-uppercase font-weight-bold">Tin cùng chuyên mục</h5>
                                            </div>
                                            <div>
                                                <Row className=" mt-2">
                                                    <ul className="list-group list-group-flush">

                                                    </ul>
                                                </Row>
                                            </div>
                                        </div>
                                    </Col>
                                </Row>
                                <Row>
                                    <Col>

                                    </Col>
                                </Row>
                            </Col>
                        </Row>
                    </div>
                </Container>
            </div>
        );
    }

}

export default ContentDetail;
